```javascript
// src/tools/context/index.js
const sessionManager = require('../../context/sessionManager');
const { createEnhancedResponse } = require('../../utils/responses');

/**
 * Get the current session context
 * @returns {Object} Enhanced response with session context
 */
function getSessionContext() {
  const sessionState = sessionManager.getSessionState();

  return createEnhancedResponse(
    { success: true, data: sessionState },
    {
      suggestions: [
        'Use this context to understand the current state of your development environment',
        'Check which files have been modified in this session'
      ],
      relatedTools: ['set_user_preference', 'get_recent_operations']
    },
    { operationType: 'context_retrieval' }
  );
}

/**
 * Set a user preference
 * @param {string} key - Preference key
 * @param {any} value - Preference value
 * @returns {Object} Enhanced response
 */
function setUserPreference(key, value) {
  sessionManager.setUserPreference(key, value);

  return createEnhancedResponse(
    { success: true, message: `Preference '${key}' set successfully` },
    {
      suggestions: ['You can retrieve this preference in future operations'],
      relatedTools: ['get_session_context']
    },
    { operationType: 'preference_update' }
  );
}

/**
 * Get recent operations history
 * @param {number} limit - Number of operations to retrieve
 * @returns {Object} Enhanced response with recent operations
 */
function getRecentOperations(limit = 10) {
  const operations = sessionManager.getSessionState().operationHistory
    .slice(-limit);

  return createEnhancedResponse(
    { success: true, data: operations },
    {
      suggestions: [
        'Review your recent actions to understand the current context',
        'Consider how these operations relate to your current task'
      ],
      relatedTools: ['get_session_context']
    },
    { operationType: 'history_retrieval' }
  );
}

module.exports = {
  get_session_context: getSessionContext,
  set_user_preference: setUserPreference,
  get_recent_operations: getRecentOperations
};
```

1. Implement check_team_activity tool to detect when others are working on related code
2. Create find_related_work tool to identify related branches and PRs
3. Develop suggest_reviewers tool based on file ownership

```javascript
// src/tools/team/index.js
const { execGit } = require('../../utils/git-helpers');
const { createEnhancedResponse } = require('../../utils/responses');

/**
 * Check for team activity on related files and branches
 * @param {Array} files - Files to check for activity
 * @returns {Object} Enhanced response with team activity information
 */
async function checkTeamActivity(files = []) {
  // Get recent commits by other team members
  const recentCommits = await execGit('log', ['--author=^(?!$(git config user.name)$)', '--pretty=format:%an|%ae|%h|%s', '-n', '10']);

  // Parse commit information
  const commits = recentCommits.split('\n')
    .filter(line => line.trim())
    .map(line => {
      const [name, email, hash, subject] = line.split('|');
      return { name, email, hash, subject };
    });

  // Check if any commits touch the specified files
  const relatedActivity = [];
  for (const commit of commits) {
    const affectedFiles = await execGit('show', ['--name-only', '--pretty=format:', commit.hash]);
    const fileList = affectedFiles.split('\n').filter(f => f.trim());

    const hasRelatedChanges = files.length === 0 || 
      files.some(file => fileList.includes(file));

    if (hasRelatedChanges) {
      relatedActivity.push({
        ...commit,
        affectedFiles: fileList
      });
    }
  }

  return createEnhancedResponse(
    { success: true, data: relatedActivity },
    {
      suggestions: [
        'Consider reaching out to team members working on related files',
        'Check for potential conflicts before making changes'
      ],
      risks: relatedActivity.length > 0 ? 
        ['Other team members have recently modified these files'] : [],
      relatedTools: ['find_related_work', 'suggest_reviewers']
    },
    { operationType: 'team_activity_check' }
  );
}

/**
 * Find related work (branches, PRs) based on files or keywords
 * @param {Object} options - Search options
 * @returns {Object} Enhanced response with related work
 */
async function findRelatedWork(options = {}) {
  const { files = [], keywords = [] } = options;

  // Find related branches
  const branches = await execGit('branch', ['-a', '--format=%(refname:short)']);
  const branchList = branches.split('\n').filter(b => b.trim());

  // Find related PRs using GitHub CLI if available
  let pullRequests = [];
  try {
    const prOutput = await execCommand('gh pr list --json number,title,url,author');
    pullRequests = JSON.parse(prOutput);
  } catch (error) {
    // GitHub CLI not available or not in a GitHub repo
  }

  // Filter based on keywords
  const filteredBranches = branchList.filter(branch => 
    keywords.some(keyword => branch.includes(keyword)));

  const filteredPRs = pullRequests.filter(pr => 
    keywords.some(keyword => pr.title.includes(keyword)));

  return createEnhancedResponse(
    { 
      success: true, 
      data: { 
        relatedBranches: filteredBranches,
        relatedPRs: filteredPRs 
      } 
    },
    {
      suggestions: [
        'Review related work before starting your changes',
        'Consider collaborating with others working on similar features'
      ],
      relatedTools: ['check_team_activity', 'suggest_reviewers']
    },
    { operationType: 'related_work_search' }
  );
}

/**
 * Suggest reviewers based on file ownership
 * @param {Array} files - Files to find reviewers for
 * @returns {Object} Enhanced response with suggested reviewers
 */
async function suggestReviewers(files = []) {
  if (files.length === 0) {
    return createEnhancedResponse(
      { success: false, message: 'No files specified' },
      { suggestions: ['Specify files to get reviewer suggestions'] },
      { operationType: 'reviewer_suggestion' }
    );
  }

  // Use git blame to find potential reviewers
  const reviewers = new Map();

  for (const file of files) {
    try {
      const blame = await execGit('blame', ['--porcelain', file]);
      const authors = blame.split('\n')
        .filter(line => line.startsWith('author '))
        .map(line => line.replace('author ', ''));

      // Count occurrences of each author
      authors.forEach(author => {
        reviewers.set(author, (reviewers.get(author) || 0) + 1);
      });
    } catch (error) {
      // File might not exist or not be tracked
    }
  }

  // Convert to array and sort by contribution count
  const suggestedReviewers = Array.from(reviewers.entries())
    .map(([name, count]) => ({ name, count }))
    .sort((a, b) => b.count - a.count);

  return createEnhancedResponse(
    { success: true, data: suggestedReviewers },
    {
      suggestions: [
        'Consider adding these reviewers to your pull request',
        'Reviewers are suggested based on their contributions to these files'
      ],
      relatedTools: ['check_team_activity', 'find_related_work']
    },
    { operationType: 'reviewer_suggestion', affectedFiles: files }
  );
}

module.exports = {
  check_team_activity: checkTeamActivity,
  find_related_work: findRelatedWork,
  suggest_reviewers: suggestReviewers
};
```

1. Implement analyze_operation_risk tool to assess potential risks of operations
2. Create check_for_conflicts tool to detect potential merge conflicts
3. Develop validate_preconditions tool to ensure operations can be performed safely

```javascript
// src/tools/safety/index.js
const { execGit } = require('../../utils/git-helpers');
const { createEnhancedResponse } = require('../../utils/responses');
const sessionManager = require('../../context/sessionManager');

/**
 * Analyze the risk of a proposed operation
 * @param {Object} operation - Operation details
 * @returns {Object} Enhanced response with risk analysis
 */
async function analyzeOperationRisk(operation = {}) {
  const { type, files = [], branch } = operation;
  const risks = [];
  const suggestions = [];

  // Check for uncommitted changes
  const status = await execGit('status', ['--porcelain']);
  const hasUncommittedChanges = status.trim().length > 0;

  if (hasUncommittedChanges) {
    risks.push('You have uncommitted changes that might be affected by this operation');
    suggestions.push('Consider committing or stashing your changes first');
  }

  // Check for specific operation risks
  switch (type) {
    case 'branch_switch':
      if (branch) {
        const branchExists = await execGit('branch', ['--list', branch]);
        if (!branchExists.trim()) {
          risks.push(`Branch '${branch}' does not exist locally`);
          suggestions.push('Use github_flow_start to create a new branch');
        }
      }
      break;
    case 'file_modification':
      if (files.length > 0) {
        // Check if files are being modified by others
        const teamActivity = await checkTeamActivity(files);
        if (teamActivity.result.data.length > 0) {
          risks.push('These files have recent changes by other team members');
          suggestions.push('Coordinate with team members to avoid conflicts');
        }
      }
      break;
    case 'commit':
      // No specific risks for commit operation
      break;
    default:
      risks.push('Unknown operation type, cannot analyze risks');
  }

  const riskLevel = risks.length === 0 ? 'low' : 
                   risks.length < 3 ? 'medium' : 'high';

  return createEnhancedResponse(
    { 
      success: true, 
      data: { 
        riskLevel,
        risks,
        operation 
      } 
    },
    {
      suggestions,
      relatedTools: ['check_for_conflicts', 'validate_preconditions']
    },
    { operationType: 'risk_analysis' }
  );
}

/**
 * Check for potential conflicts with remote changes
 * @param {Array} files - Files to check for conflicts
 * @returns {Object} Enhanced response with conflict information
 */
async function checkForConflicts(files = []) {
  // Fetch latest changes from remote
  await execGit('fetch', ['origin']);

  const currentBranch = (await execGit('branch', ['--show-current'])).trim();
  const remoteBranch = `origin/${currentBranch}`;

  // Check if remote branch exists
  const remoteBranches = await execGit('branch', ['-r']);
  const remoteExists = remoteBranches.includes(remoteBranch);

  if (!remoteExists) {
    return createEnhancedResponse(
      { 
        success: true, 
        data: { 
          hasConflicts: false,
          message: 'No remote branch to check conflicts against' 
        } 
      },
      {
        suggestions: ['Your branch is not yet pushed to remote']
      },
      { operationType: 'conflict_check' }
    );
  }

  // Check for potential conflicts
  const diffOutput = await execGit('diff', [`${remoteBranch}...${currentBranch}`, '--name-only']);
  const conflictingFiles = diffOutput.split('\n')
    .filter(f => f.trim())
    .filter(f => files.length === 0 || files.includes(f));

  const hasConflicts = conflictingFiles.length > 0;

  return createEnhancedResponse(
    { 
      success: true, 
      data: { 
        hasConflicts,
        conflictingFiles 
      } 
    },
    {
      suggestions: hasConflicts ? [
        'Pull latest changes before proceeding',
        'Coordinate with team members working on these files'
      ] : [],
      risks: hasConflicts ? [
        'Potential merge conflicts detected',
        'Remote changes might overwrite your work'
      ] : [],
      relatedTools: ['analyze_operation_risk', 'check_team_activity']
    },
    { operationType: 'conflict_check', affectedFiles: conflictingFiles }
  );
}

/**
 * Validate preconditions for an operation
 * @param {Object} operation - Operation details
 * @returns {Object} Enhanced response with validation results
 */
async function validatePreconditions(operation = {}) {
  const { type, files = [], branch } = operation;
  const validations = [];
  const failures = [];

  // Common validations
  try {
    // Check if in a git repository
    await execGit('rev-parse', ['--is-inside-work-tree']);
    validations.push('In a valid git repository');
  } catch (error) {
    failures.push('Not in a valid git repository');
    return createEnhancedResponse(
      { 
        success: false, 
        data: { validations, failures },
        message: 'Precondition check failed: Not in a git repository'
      },
      {
        suggestions: ['Initialize a git repository before proceeding']
      },
      { operationType: 'precondition_validation' }
    );
  }

  // Operation-specific validations
  switch (type) {
    case 'branch_switch':
      if (!branch) {
        failures.push('No branch specified for branch switch operation');
      }
      break;
    case 'file_modification':
      if (files.length === 0) {
        failures.push('No files specified for file modification operation');
      } else {
        // Check if files exist
        for (const file of files) {
          try {
            await execGit('ls-files', ['--error-unmatch', file]);
            validations.push(`File exists: ${file}`);
          } catch (error) {
            failures.push(`File does not exist: ${file}`);
          }
        }
      }
      break;
    case 'commit':
      // Check if there are changes to commit
      const status = await execGit('status', ['--porcelain']);
      if (!status.trim()) {
        failures.push('No changes to commit');
      } else {
        validations.push('Changes available to commit');
      }
      break;
    default:
      failures.push('Unknown operation type');
  }

  const success = failures.length === 0;

  return createEnhancedResponse(
    { 
      success, 
      data: { validations, failures },
      message: success ? 'All preconditions satisfied' : 'Some preconditions failed'
    },
    {
      suggestions: success ? [
        'Proceed with the operation',
        'Consider running a risk analysis before proceeding'
      ] : [
        'Address the failed preconditions before proceeding'
      ],
      relatedTools: ['analyze_operation_risk', 'check_for_conflicts']
    },
    { operationType: 'precondition_validation' }
  );
}

module.exports = {
  analyze_operation_risk: analyzeOperationRisk,
  check_for_conflicts: checkForConflicts,
  validate_preconditions: validatePreconditions
};
```

1. Enhance github_flow_start tool with contextual information
2. Update auto_commit tool with rich responses
3. Add semantic descriptions and usage examples to all GitHub flow tools
4. Follow TDD approach with tests written before implementation
5. Ensure 90%+ test coverage with Jest

```javascript
// src/tools/github-flow/index.js
const { execGit } = require('../../utils/git-helpers');
const { createEnhancedResponse } = require('../../utils/responses');
const sessionManager = require('../../context/sessionManager');
const { enhanceResponse } = require('../../enhancers');

/**
 * Start a new feature branch following GitHub Flow
 * @param {Object} options - Branch options
 * @returns {Object} Enhanced response
 */
async function githubFlowStart(options = {}) {
  const { name, base = 'main' } = options;

  if (!name) {
    return createEnhancedResponse(
      { success: false, message: 'Branch name is required' },
      {
        suggestions: [
          'Provide a descriptive branch name for your feature',
          'Use kebab-case for branch names (e.g., add-login-feature)'
        ]
      },
      { operationType: 'branch_creation' }
    );
  }

  try {
    // Fetch latest changes
    await execGit('fetch', ['origin']);

    // Check if base branch exists
    const branches = await execGit('branch', ['-a']);
    const baseExists = branches.includes(base) || branches.includes(`remotes/origin/${base}`);

    if (!baseExists) {
      return createEnhancedResponse(
        { success: false, message: `Base branch '${base}' does not exist` },
        {
          suggestions: [
            'Check available branches with git branch -a',
            'Use main or master as the base branch'
          ],
          risks: ['Creating a branch from a non-existent base can lead to issues']
        },
        { operationType: 'branch_creation' }
      );
    }

    // Check if branch already exists
    const branchExists = branches.includes(name) || branches.includes(`remotes/origin/${name}`);

    if (branchExists) {
      return createEnhancedResponse(
        { success: false, message: `Branch '${name}' already exists` },
        {
          suggestions: [
            'Choose a different branch name',
            'Use git checkout to switch to the existing branch'
          ],
          risks: ['Creating a branch with the same name can cause conflicts']
        },
        { operationType: 'branch_creation' }
      );
    }

    // Create and checkout the new branch
    await execGit('checkout', ['-b', name, `origin/${base}`]);

    // Update session context
    sessionManager.updateCurrentBranch(name);
    sessionManager.recordOperation({
      type: 'branch_creation',
      branch: name,
      base
    });

    return createEnhancedResponse(
      { 
        success: true, 
        message: `Created and switched to branch '${name}' based on '${base}'` 
      },
      {
        suggestions: [
          'Make your changes and use auto_commit to commit them',
          'Push your branch with git push -u origin ' + name,
          'Create a pull request when your feature is complete'
        ],
        bestPractices: [
          'Keep commits small and focused on a single change',
          'Write descriptive commit messages'
        ],
        relatedTools: ['auto_commit', 'check_for_conflicts']
      },
      { 
        operationType: 'branch_creation',
        branchName: name,
        baseBranch: base
      }
    );
  } catch (error) {
    return createEnhancedResponse(
      { success: false, message: `Failed to create branch: ${error.message}` },
      {
        suggestions: [
          'Make sure you have permission to create branches',
          'Check your internet connection'
        ],
        risks: ['Your local repository might be in an inconsistent state']
      },
      { operationType: 'branch_creation' }
    );
  }
}

/**
 * Automatically commit changes with a descriptive message
 * @param {Object} options - Commit options
 * @returns {Object} Enhanced response
 */
async function autoCommit(options = {}) {
  const { message, files = [] } = options;

  if (!message) {
    return createEnhancedResponse(
      { success: false, message: 'Commit message is required' },
      {
        suggestions: [
          'Provide a descriptive commit message',
          'Start with a verb (e.g., "Add", "Fix", "Update")'
        ]
      },
      { operationType: 'commit' }
    );
  }

  try {
    // Check if there are changes to commit
    const status = await execGit('status', ['--porcelain']);

    if (!status.trim()) {
      return createEnhancedResponse(
        { success: false, message: 'No changes to commit' },
        {
          suggestions: [
            'Make changes to files before committing',
            'Use git status to check the status of your working directory'
          ]
        },
        { operationType: 'commit' }
      );
    }

    // Add files if specified, otherwise add all changes
    if (files.length > 0) {
      await execGit('add', files);
    } else {
      await execGit('add', ['.']);
    }

    // Commit changes
    await execGit('commit', ['-m', message]);

    // Get affected files for context
    const commitInfo = await execGit('show', ['--name-only', '--pretty=format:', 'HEAD']);
    const affectedFiles = commitInfo.split('\n').filter(f => f.trim());

    // Update session context
    sessionManager.trackModifiedFiles(affectedFiles);
    sessionManager.recordOperation({
      type: 'commit',
      message,
      files: affectedFiles
    });

    return createEnhancedResponse(
      { 
        success: true, 
        message: `Changes committed: ${message}`,
        data: { affectedFiles }
      },
      {
        suggestions: [
          'Push your changes with git push',
          'Continue making changes for your feature'
        ],
        bestPractices: [
          'Keep commits small and focused on a single change',
          'Consider creating a pull request when your feature is complete'
        ],
        relatedTools: ['github_flow_start', 'check_for_conflicts']
      },
      { 
        operationType: 'commit',
        commitMessage: message,
        affectedFiles
      }
    );
  } catch (error) {
    return createEnhancedResponse(
      { success: false, message: `Failed to commit changes: ${error.message}` },
      {
        suggestions: [
          'Check if you have permission to commit to this repository',
          'Ensure your git configuration is correct'
        ],
        risks: ['Your changes have not been committed']
      },
      { operationType: 'commit' }
    );
  }
}

// Add semantic descriptions to tools
const tools = {
  github_flow_start: {
    function: githubFlowStart,
    description: 'Start a new feature branch following GitHub Flow',
    usage: 'Use this tool when beginning work on a new feature or bugfix',
