const { execCommand } = require('../../utils/command-helpers');
const { createEnhancedResponse } = require('../../utils/responses');
const sessionManager = require('../../context/sessionManager');

/**
 * Run a test suite or specific tests
 * @param {Object} options - Test options
 * @returns {Object} Enhanced response
 */
async function runTests(options = {}) {
  const { testPath, watch = false } = options;

  try {
    // Determine test command based on project setup
    let command = 'npm test';
    if (testPath) {
      command += ` -- ${testPath}`;
    }
    if (watch) {
      command += ' -- --watch';
    }

    // Run tests
    const output = await execCommand(command);
    const success = !output.includes('FAIL') && !output.includes('ERROR');

    // Update session context
    sessionManager.recordOperation({
      type: 'test_run',
      testPath,
      success
    });

    return createEnhancedResponse(
      { 
        success, 
        message: success ? 'Tests completed successfully' : 'Tests failed',
        data: { output }
      },
      {
        suggestions: success ? [
          'Commit your changes if tests are passing',
          'Continue with your development workflow'
        ] : [
          'Fix failing tests before committing',
          'Check test output for specific errors'
        ],
        bestPractices: [
          'Run tests before committing changes',
          'Write tests for new features'
        ],
        relatedTools: ['auto_commit', 'analyze_code']
      },
      { 
        operationType: 'test_run',
        testPath
      }
    );
  } catch (error) {
    return createEnhancedResponse(
      { success: false, message: `Failed to run tests: ${error.message}` },
      {
        suggestions: [
          'Check if test dependencies are installed',
          'Verify your test configuration'
        ],
        risks: ['Your code may have issues that tests would have caught']
      },
      { operationType: 'test_run' }
    );
  }
}

/**
 * Analyze code for quality issues
 * @param {Object} options - Analysis options
 * @returns {Object} Enhanced response
 */
async function analyzeCode(options = {}) {
  const { path = '.', fix = false } = options;

  try {
    // Determine if ESLint is available
    let command = `npx eslint ${path}`;
    if (fix) {
      command += ' --fix';
    }

    // Run analysis
    const output = await execCommand(command);
    const success = !output.includes('error') || output.trim() === '';

    // Parse issues if present
    const issues = output.trim() ? 
      output.split('\n')
        .filter(line => line.includes('error') || line.includes('warning'))
        .map(line => line.trim()) : 
      [];

    // Update session context
    sessionManager.recordOperation({
      type: 'code_analysis',
      path,
      issueCount: issues.length
    });

    return createEnhancedResponse(
      { 
        success, 
        message: success ? 'No code quality issues found' : 'Code quality issues detected',
        data: { issues, output }
      },
      {
        suggestions: success ? [
          'Proceed with committing your changes',
          'Run tests to ensure functionality'
        ] : [
          'Fix code quality issues before committing',
          'Consider using --fix option to automatically fix some issues'
        ],
        bestPractices: [
          'Regularly analyze code for quality issues',
          'Maintain consistent code style across the project'
        ],
        relatedTools: ['run_tests', 'auto_commit']
      },
