// src/tools/utilities/index.js
const { execGit } = require('../../utils/git-helpers');
const { execCommand } = require('../../utils/command-helpers');
const { createEnhancedResponse } = require('../../utils/responses');
const sessionManager = require('../../context/sessionManager');

/**
 * Get the status of the current repository
 * @returns {Object} Enhanced response
 */
async function getStatus() {
  try {
    // Get git status
    const status = await execGit('status', ['--porcelain']);
    const branchInfo = await execGit('branch', ['--show-current']);
    const currentBranch = branchInfo.trim();

    // Parse status output
    const files = status.split('\n')
      .filter(line => line.trim())
      .map(line => {
        const state = line.substring(0, 2).trim();
        const file = line.substring(3).trim();
        return { state, file };
      });

    // Categorize files
    const modified = files.filter(f => f.state.includes('M')).map(f => f.file);
    const added = files.filter(f => f.state.includes('A')).map(f => f.file);
    const deleted = files.filter(f => f.state.includes('D')).map(f => f.file);
    const untracked = files.filter(f => f.state.includes('??')).map(f => f.file);

    // Update session context
    sessionManager.updateCurrentBranch(currentBranch);
    sessionManager.trackModifiedFiles([...modified, ...added]);

    return createEnhancedResponse(
      { 
        success: true, 
        data: { 
          currentBranch,
          modified,
          added,
          deleted,
          untracked,
          clean: files.length === 0
        } 
      },
      {
        suggestions: files.length > 0 ? [
          'Consider committing your changes',
          'Use auto_commit to commit with a descriptive message'
        ] : [
          'Your working directory is clean',
          'Start making changes for your feature'
        ],
        relatedTools: ['auto_commit', 'github_flow_start']
      },
      { operationType: 'status_check' }
    );
  } catch (error) {
    return createEnhancedResponse(
      { success: false, message: `Failed to get status: ${error.message}` },
      {
        suggestions: [
          'Check if you are in a git repository',
          'Initialize a git repository if needed'
        ],
        risks: ['Cannot determine the state of your working directory']
      },
      { operationType: 'status_check' }
    );
  }
}

/**
 * Get information about the current repository
 * @returns {Object} Enhanced response
 */
async function getRepoInfo() {
  try {
    // Get repository information
    const remoteUrl = await execGit('remote', ['get-url', 'origin']);
    const currentBranch = (await execGit('branch', ['--show-current'])).trim();
    const lastCommit = await execGit('log', ['-1', '--pretty=format:%h|%an|%s']);

    // Parse last commit
    const [hash, author, subject] = lastCommit.split('|');

    // Get branch list
    const branches = (await execGit('branch', [])).split('\n')
      .map(b => b.replace('*', '').trim())
      .filter(b => b);

    // Update session context
    sessionManager.updateCurrentBranch(currentBranch);

    return createEnhancedResponse(
      { 
        success: true, 
        data: { 
          remoteUrl: remoteUrl.trim(),
          currentBranch,
          lastCommit: { hash, author, subject },
          branches
        } 
      },
      {
        suggestions: [
          'Use this information to understand your repository context',
          'Check which branch you are currently on'
        ],
        relatedTools: ['get_status', 'github_flow_start']
      },
      { operationType: 'repo_info' }
    );
  } catch (error) {
    return createEnhancedResponse(
      { success: false, message: `Failed to get repository info: ${error.message}` },
      {
        suggestions: [
          'Check if you are in a git repository',
          'Initialize a git repository if needed'
        ],
        risks: ['Cannot determine information about your repository']
      },
      { operationType: 'repo_info' }
    );
  }
}

