// src/tools/index.js
const githubFlowTools = require('./github-flow');
const automationTools = require('./automation');
const utilityTools = require('./utilities');
const contextTools = require('./context');
const teamTools = require('./team');
const safetyTools = require('./safety');

/**
 * Register all tools with metadata and categorization
 * @returns {Object} Registered tools organized by category
 */
function registerTools() {
  const toolCategories = {
    'github-flow': {
      description: 'Tools for GitHub Flow workflow',
      tools: githubFlowTools
    },
    'automation': {
      description: 'Tools for automating development tasks',
      tools: automationTools
    },
    'utilities': {
      description: 'Utility tools for repository information',
      tools: utilityTools
    },
    'context': {
      description: 'Tools for managing session context',
      tools: contextTools
    },
    'team': {
      description: 'Tools for team collaboration',
      tools: teamTools
    },
    'safety': {
      description: 'Tools for ensuring safe operations',
      tools: safetyTools
    }
  };

  // Flatten tools with category metadata
  const tools = {};

  Object.entries(toolCategories).forEach(([category, info]) => {
    Object.entries(info.tools).forEach(([name, fn]) => {
      tools[name] = (...args) => {
        const result = fn(...args);
        return {
          ...result,
          metadata: {
            ...result.metadata,
            category,
            categoryDescription: info.description
          }
        };
      };
    });
  });

  return tools;
}

module.exports = { registerTools };
```

