// src/index.js
const { registerTools } = require('./tools');
const sessionManager = require('./context/sessionManager');

/**
 * Initialize the MCP server with all tools and context
 * @param {Object} config - Server configuration
 * @returns {Object} MCP server instance
 */
function initializeMCPServer(config = {}) {
  // Register all tools
  const tools = registerTools();

  // Initialize session with config
  if (config.initialContext) {
    Object.entries(config.initialContext).forEach(([key, value]) => {
      sessionManager.setUserPreference(key, value);
    });
  }

  return {
    /**
     * Get all registered tools
     * @returns {Object} All registered tools
     */
    getTools() {
      return tools;
    },

    /**
     * Get a specific tool by name
     * @param {string} name - Tool name
     * @returns {Function|undefined} Tool function if found
     */
    getTool(name) {
      return tools[name];
    },

    /**
     * Get the current session context
     * @returns {Object} Current session state
     */
    getSessionContext() {
      return sessionManager.getSessionState();
    },

    /**
     * Reset the session context
     * @returns {void}
     */
    resetSession() {
      sessionManager.resetSession();
    },

    /**
     * Get metadata about all available tools
     * @returns {Object} Tool metadata by category
     */
    getToolMetadata() {
      const metadata = {};

      Object.entries(tools).forEach(([name, fn]) => {
        // Call the tool with empty args to get metadata
        try {
          const result = fn({});
          metadata[name] = {
            category: result.metadata.category,
            description: result.metadata.toolDescription,
            usage: result.metadata.whenToUse,
            examples: result.metadata.examples,
            solves: result.metadata.whatItSolves
          };
        } catch (error) {
          // Skip tools that can't be called without args
        }
      });

      return metadata;
    }
  };
}

module.exports = initializeMCPServer;
```

